mapper.insert:
INSERT INTO post(
category_id
, writer
, password
, title
, content
, reg_date
)
VALUES(
#{categoryId}
, #{writer}
, SHA2(#{password},256)
, #{title}
, #{content}
, #{regDate}
)

mapper.update:
UPDATE post
SET writer=#{writer}, title=#{title}, content=#{content}, mod_date=#{modDate}
WHERE post_id=#{postId}

mapper.select:
SELECT p.post_id AS postId , c.category_id AS categoryId , c.name AS categoryName , p.writer AS writer , p.title AS title , p.content AS content , p.reg_date AS regDate , p.mod_date AS modDate , p.hit AS hit , (SELECT COUNT(file_id) FROM FILE f WHERE f.post_id=p.post_id) AS isFileExists FROM post p JOIN category c WHERE p.category_id=c.category_id
AND p.category_id=#{criteria.categoryId}
AND (p.title LIKE '%${criteria.searchWord}%' OR p.writer LIKE '%${criteria.searchWord}%' OR p.content LIKE '%${criteria.searchWord}%')
AND (p.reg_date BETWEEN #{criteria.startDate} AND #{criteria.endDate})
AND p.reg_date >= #{criteria.startDate}
AND p.reg_date <= #{criteria.endDate}
ORDER BY p.reg_date DESC LIMIT #{offset},#{limit}

SELECT COUNT(post_id)
FROM post


        (title LIKE '%${searchWord}%'
            OR writer LIKE '%${searchWord}%'
            OR content LIKE '%${searchWord}%')


        AND category_id=#{categoryId}



            AND reg_date BETWEEN #{startDate} AND #{endDate}


            AND reg_date >= #{startDate}


            AND reg_date <= #{endDate}
